    function getKeys()
    {
       global $keys_file;
        $fp = fopen($keys_file, "r");
        $keys = null;
        if (flock($fp, LOCK_SH)) {
            $keys = fread($fp, filesize($keys_file));
            flock($fp, LOCK_UN);
        }
        fclose($fp);
        return $keys;
    }"]}

