    		foreach($this->arr_done as $a)
    			if($a['Quantity'] == false || $a['Value'] == false)
    				return false;
    		return true;
    	}
    	private function isCellDone($id, $fieldName) {
    		return $this->arr_done[$id][$fieldName];
    	}
